import axios from 'axios';
import React, { useState,useEffect } from 'react'
import { Link, useNavigate,useParams } from 'react-router-dom';

const AddAction = () => {
    let navigate=useNavigate();
    const[tasks,setTask]=useState([])
    const [action,setAction]=useState("");
    const {wid,tid}=useParams();
    // console.log(wid);
    // console.log(tid);

    useEffect(() => {
        loadUsers();

    }, []
    );
    const loadUsers = async () => {
        const result = await axios.get("http://localhost:8080/api/HR/get");
        setTask(result.data);

    }

    var bodyFormData = new FormData();
    //bodyFormData.append('name', wf);
    const onSubmit=async(e)=>{
        axios({
            method: "post",
            url: `http://localhost:9191/workflow/addWorkflow`,
            data: bodyFormData,
            headers: { "Content-Type": "multipart/form-data" },
          })
            .then(function (response) {
              //handle success
              console.log(response);
            })
            .catch(function (response) {
              //handle error
              console.log(response);
            });
        navigate("/");
    }


  return (
    <div className='admincreate'>
        <div className='container'>
        <div className='col-md-6 offset-md-3 border rounded p-4 mt-2 shadow'>
        <form onSubmit={(e)=>onSubmit(e)}>
            <div className='mb-3'>
                <label htmlFor='Name' className='form-label'>
                    Action Name
                </label>
                <input
                    type={"text"}
                    className="form-control"
                    placeholder='Enter Worklfow Name'
                    name="wf_name"
                    //value="wf_name"
                    onChange={(e)=>setAction(e.target.value)}
                    required
                />
            </div>


            <button type='submit'  className='btn btn-outline-primary'>Submit</button>
            <Link className='btn btn-outline-danger mx-2' to="/">Cancel</Link>
        </form>
        </div>
        </div>
    </div>
  )
}

export default AddAction
