import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import Table from 'react-bootstrap/Table';

export default function Tasks() {
    let navigate = useNavigate()
    const [tasks,setTask]=useState([])
    const [data,setData]=useState([])
    const [actions,setAction]=useState([])
    const[cat,setSelectedCategory]=useState([])

    var bodyFormData = new FormData();
    const{id}=useParams();
    useEffect(()=>{
        loadUser();

    },[]);

    const onSubmit=async(e)=>{
        axios({
            method: "post",
            url: url,
            data: bodyFormData,
            headers: { "Content-Type": "multipart/form-data" },
          })
          .catch(error => {
            alert(error);
            console.log(error)
            //navigate(`/`);

        });
        navigate(`/viewtask/${id}`);
        navigate(0);
    };



    const loadUser=async()=>{
        const result=await axios.get(`http://localhost:9191/taskInstance/fetchTaskInstances/1`)
        setTask(result.data);

        const resultact = axios.get(`http://localhost:9191/action/viewActions/1`)
        setAction(resultact.data);

    }


  return (
    <div className='container'>
    <div className='row'>
        <div className='col-md-6 offset-md-3'>
        <div className='py-4'>
                <Table striped bordered hover >
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Comment</th>
                            <th scope="col">Attchments</th>
                            <th scope="col">Status</th>
                            <th scope="col">Desc</th>
                            <th scope="col">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tasks.map((task, index) => (

                                <tr>
                                    <th scope="row" key={index}>{index + 1}</th>
                                    <td>{task.comments}</td>
                                    <td>{task.attachments}</td>
                                    <td>{task.status}</td>
                                    <td>{task.workflowInstance.description}</td>
                                    {/* <td>{task.workflowInstance.workflow.name}</td> */}
                                    <td>

                                    <form onSubmit={(e)=>onSubmit(e)}>
                                        <div className='mb-3'>
                                            <label htmlFor='Name' className='form-label'>
                                                Description
                                            </label>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder='Enter Task Name'
                                                name="wf_name"
                                                //value="wf_name"
                                                onChange={(e)=>setTask(e.target.value)}
                                                required
                                            />
                                        </div>

                                        <button type='submit'  className='btn btn-outline-primary'>Submit</button>
                                        <Link className='btn btn-outline-danger mx-2' to={`/viewtask/${id}`}>Cancel</Link>
                                        </form>

                                        {/* <Link className="btn btn-primary mx-2" to={`/usermain`}>
                                            Action
                                        </Link> */}

                                    </td>
                                </tr>
                            )
                            )
                        }
                    </tbody>
                </Table>

            </div>

            <Link className='btn btn-primary my-2' to={"/usermain"}>Back to Home</Link>
        </div>
        </div>
        <div className="footer">
                <p>Made with ‚ù§Ô∏è and üßë‚Äçüíª by <i>Harsh Tripathi</i></p>
            </div>
        </div>
  )
}
